package com.yuwei.face.util;import android.annotation.SuppressLint;import android.app.Notification;import android.app.NotificationManager;import android.content.Context;import android.os.Bundle;/** * 信息提示工具类 * @author YWWH-113 * */public class NotifyUtil {        private static NotificationManager nm = null;    private static final String YUWEI_NOTIFY_TYPE = "yuwei_notify_type";    /**     * 顶部信息提示     * @param _c     * @param _msg     */    @SuppressLint("NewApi")    public static void showTopMessage(Context _c,String _msg) {        if(_c == null) return;        if(nm != null)            nm.cancel(1);        else            nm = (NotificationManager) _c.getSystemService(Context.NOTIFICATION_SERVICE);        Bundle bundle = new Bundle();        bundle.putInt(YUWEI_NOTIFY_TYPE, 2);        Notification notification = new Notification.Builder(_c)                .setExtras(bundle)                .setContentText(_msg)                .build();        nm.notify(1, notification);    }}